import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  name: "mainPage",
  data() {
    return {
      mainBlogs: [],
      selectId: null
    };
  },
  computed: {
    selectBlog() {
      for (let blog of this.mainBlogs) {
        if (blog.id === this.selectId) {
          return blog;
        }
      }
      return this.$store.state.missingBlog;
    }
  },
  methods: {
    getBlog() {
      axios.get('/server/allBlogs').then(response => {
        this.mainBlogs.push(response.data);
      }).catch(e => {
        console.log(e);
      });
    },
    changeSelectId(index) {
      this.selectId = index;
    }
  },
  mounted() {
    this.getBlog();
  }
};
