import axios from "axios";

async function getHttp(url){
    let value;
    axios.get(url)
        .then((response)=>{
            value = response.data;
        })
        .catch((e)=>{
            throw e;
        })
        .finally(()=>{
            console.log(value);
            return value;
        })
}

export default {
    install(app){
        app.config.globalProperties.$httpGet=getHttp;
    }
}
