import {defineStore} from "pinia/dist/pinia";

export const useMyBlogsStore= defineStore('myBlogs',{
    state(){
        return{
            // blogList: localStorage.getItem('myBlogs'),
            // 所有的博客列表
            blogList:[],
            // 选中的博客id
            blogIdNow: null,
        }
    },
    getters(){
        return {
            getBlogNowById(state){
                for (let blog of state.blogList){
                    if(blog.id === state.blogIdNow){
                        return blog;
                    }
                }
                return null;
            },
            test:(state)=>{
                console.log(state);
            }
        }
        // 根据选中博客id获取blog对象

    },
    methods:{
        test2(){
            console.log(2)
        }
    }
})
