import axios from "axios";

async function getHttp(url){
    let value;
    axios.get(url)
        .then((response)=>{
            value = response.data;
        })
        .then(()=>{
            this.error=false;
        })
        .catch((e)=>{
            throw e;
        })
    await value;
}

export default {
    install(app){
        app.config.globalProperties.$httpGet=getHttp();
    }
}
