import axios from "axios";

function getHttp(url){
    axios.get(url)
        .then((response)=>{
            return response.data;
        })
        .catch((e)=>{
            throw e;
        })
}

export default {
    install(app){
        app.config.globalProperties.$httpGet=getHttp;
    }
}
