import axios from "axios";

async function getHttp(url){
    let value;
    await axios.get(url)
        .then((response)=>{
            value = response.data;
        })
        .catch((e)=>{
            throw e;
        })
    await value;
}

export default {
    install(app){
        app.config.globalProperties.$httpGet=getHttp;
    }
}
