import "core-js/modules/es.array.push.js";
import axios from 'axios';
import { marked } from "marked";
export default {
  name: "mainPage",
  data() {
    return {
      mainBlogs: [],
      selectId: null
    };
  },
  computed: {
    selectBlogView() {
      for (let blog of this.mainBlogs) {
        if (blog.id === this.selectId) {
          return marked.parse(blog.content);
        }
      }
      return marked(this.$store.state.missingBlog.content);
    }
  },
  methods: {
    getBlog() {
      axios.get('http://43.142.78.228:4444/allBlogs').then(response => {
        this.mainBlogs.push(response.data);
      }).catch(e => {
        alert(e);
      });
    },
    changeSelectId(index) {
      this.selectId = index;
    }
  },
  mounted() {
    this.getBlog();
  }
};
