import {createStore} from "vuex";

export const myBlogsStore= createStore({
    state(){
        return{
            // blogList: localStorage.getItem('myBlogs'),
            // 所有的博客列表
            blogList:[],
            // 选中的博客id
            blogIdNow: null,
            blogNow:null,
        }
    },
    getters:{
        // 根据选中博客id获取blog对象
        getBlogNowById:(state)=>{
            for (let blog of state.blogList){
                if(blog.id === state.blogIdNow){
                    state.blogNow = blog;
                }
            }
            state.blogNow =null;
        },
    },
})
